function _findByName(list, name) {
  const results = list.filter((item) => item.name === name);
  return results && results[0];
}

function _findById(list, id) {
  if (typeof(id) === 'string') {
    id = Number(id);
  }
  const results = list.filter((item) => item.id === id);
  return results && results[0];
}

function _intersect(array1, array2, predicate) {
  return array1.filter(function(item) {
      return predicate(array2, item);
  });
}

function _decreaseFontSize(el, unit) {
  const currentValue = Number(el.style.fontSize.substring(0, el.style.fontSize.indexOf(unit)));
  el.style.fontSize = currentValue - 1 + unit;
}

function _getFontSize(moveName, maxSize) {
  maxSize = maxSize * 0.95; // We want to fit in 95% of the size;

  const calculationDiv = document.getElementsByClassName('font-size-calculation')[0];
  calculationDiv.innerText = moveName.toUpperCase();
  calculationDiv.style.fontSize = '10px';
  while(calculationDiv.offsetWidth > maxSize) {
    _decreaseFontSize(calculationDiv, 'px');
  }
  return calculationDiv.style.fontSize;
}

function _getTypeClass(typeId) {
  return TYPE_TO_CSS_CLASS[typeId-1];
}

function _augmentPokemonsData(pokemonsData, typesData, moves, pokemonMoves) {
  return pokemonsData.map((pokemon) => {
    const currentPokemonMoves = _findById(pokemonMoves, pokemon.id);

    if (!currentPokemonMoves) {
      console.error('No moves for current pokemon')
    }

    return {
      id: pokemon.id,
      name: localeManager.translate(pokemon.key),
      key: pokemon.key,
      types: pokemon.types
                  .sort((type1, type2) => type1.slot > type2.slot)
                  .map((type) => _findById(typesData, type.id))
                  .map((type) => Object.assign({}, type, {
                    name: localeManager.translate(type.key),
                    cssClass: _getTypeClass(type.id)
                  })),
      moves: currentPokemonMoves.moves.map((pokemonMove) => _findByName(moves, pokemonMove))
    }
  });
}

function updateList(pokemons) {
  const fragment = document.createDocumentFragment();

  var i = 0;
  pokemons.forEach((pokemon) => {
    let htmlString = `<div class="pokemon" data-id="${pokemon.id}">\
      <div class="picture">\
        <img src="images/${localeManager.translate(pokemon.key, 'en').toLowerCase()}.png">\
      </div>\
      <div class="name">\
        ${pokemon.name}\
      </div>\
    </div>`;

    const el = _DOMElementFromString(htmlString);

    el.addEventListener('click', (e) => {
      const pokemonId = e.currentTarget.dataset.id;
      updateDetail(_findById(pokemonsFull, pokemonId));
      showDetail();
    });
    fragment.appendChild(el);
  });

  document.querySelector('.pokedex').appendChild(fragment);
}

function updateDetail(pokemon) {

  const defaultCPValue = 2400;
  const typesHTML = pokemon.types.sort((type1, type2) => type1.slot > type2.slot )
              .map((type) => `<div class="type ${type.cssClass}"><span class="name">${type.name}</span></div>`)
              .join('');

  const strongTypes = pokemon.types.reduce(
    (state, type) => state.concat(type.damages.double.from), []);

  const strongMoves = moves.filter(
      (move) => strongTypes.indexOf(move.type.toLowerCase()) !== -1);

  const strongMovesName = strongMoves.map(sm => sm.name);
  let strongPokemons = pokemonsFull.filter(
      (p) => {
          const movesName = p.moves.map(m => m && m.name || '');
          return movesName.reduce((state, moveName) => state || strongMovesName.indexOf(moveName) !== -1, false)
      });

  // Filter strong pokemons to remove those that are weak to the current pokemon's types
  const pokemonMovesType = pokemon.moves.map(m => m && m.type || '');
  strongPokemons = strongPokemons.filter((strongPokemon) => {
    const weaknesses = strongPokemon.types.reduce(
      (state, type) => state.concat(type.damages.double.from), []);

    return !_intersect(pokemon.types, weaknesses, (types, item) => types.indexOf(item.name) !== -1).length;
  });

  const beatenByHTML = strongPokemons.map((pokemon, i) => {
    const strongMovesNames = strongMoves.map(m => m.name);
    const move = _intersect(pokemon.moves, strongMovesNames,
      (list, item) => item && (list.indexOf(item.name) !== -1))[0];
    const mainType = pokemon.types[0].name;
    const fontSize = _getFontSize(move.name, 60);
    return `<div class="other-pokemon" data-id="${pokemon.id}">\
      <div class="picture">\
        <img src="images/${localeManager.translate(pokemon.key, 'en')}.png" />\
      </div>\
      <div class="type ${move.type.toLowerCase()}" style="font-size: ${fontSize}">\
        <span class="name">${move.name}</span>\
      </div>\
    </div>`;
  }).join('');

  const overlayDataHTML = `<div><h2 class="pokemon-name">
        ${pokemon.name}
      </h2>
      <section class="main-pokemon">
        <div class="stats">
          <div class="cp">
          </div>
          <div class="picture">
            <img src="images/${localeManager.translate(pokemon.key, 'en')}.png" />
          </div>
          <div class="types">
            ${typesHTML}
          </div>
        </div>

        <div class="counters">
          <h3 class="title title--normal">
            Can be beaten by
          </h3>

          <div class="beaten-by">
            ${beatenByHTML}
          </div>
        </div>
      </section>
    </div><div>`;

  const overlayDOMEl = _DOMElementFromString(overlayDataHTML);

  document.querySelector('.overlay__data').innerHTML = '';
  document.querySelector('.overlay__data').appendChild(overlayDOMEl);

  // Add event listeners
  Array.prototype.forEach.call(overlayDOMEl.querySelectorAll('.other-pokemon'), (el) => {
    el.addEventListener('click', (e) => {
      const pokemonId = e.currentTarget.dataset.id;
      updateDetail(_findById(pokemonsFull, pokemonId));
      showDetail();
    });
  });
}

function showDetail() {
  window.scroll(0, 0);
  document.querySelector('.overlay').classList.remove('is-hidden')
  document.querySelector('.pokedex').classList.add('is-blur')
}

function hideDetail() {
  document.querySelector('.overlay').classList.add('is-hidden')
  document.querySelector('.pokedex').classList.remove('is-blur')
}

function addKeyboardListener() {
  window.addEventListener('keyup', (e) => {
    if (e.keyCode === 27) {
      hideDetail();
    }
  })
}

function _DOMElementFromString(htmlString) {
  const div = document.createElement('div');
  div.innerHTML = htmlString;
  return div.firstChild;
}



//===== Locale Manager =====//

function LocaleManager(dictionary) {
  this._dictionary = dictionary;
  this._lang = 'en';
}

LocaleManager.prototype.setLanguage = function(lang) {
  this._lang = 'fr';
}

LocaleManager.prototype.translate = function(key, lang) {
  const translations = this._dictionary[key];
  if (!translations) {
    console.error('No translation at all for', key);
    return;
  }

  return translations[lang || this._lang];
}

var localeManager = new LocaleManager(dictionary);
localeManager.setLanguage('fr');

